import fetch from 'isomorphic-fetch'
import {getAllProjectsLinks} from '../../lib/getAllProjectsLinks'

export default function Projet({titre, description}){
    return(
        <div>
            <h1>{titre}</h1>
            <p>{description}</p>
        </div>
    ) 
}

export async function getStaticPaths() {
    const res = await fetch('http://localhost:3000/api/projets')
    const projets = await res.json()
    const paths = getAllProjectsLinks(projets)
    return{
        paths,
        fallback: false
    }
}

export async function getStaticProps({params}) {
    const res = await fetch('http://localhost:3000/api/projets')
    const projets = await res.json()
    const array = projets.filter((projets) => projets.link === params.projet)
    return {
        props: array[0]
    }
}
