import useSWR from 'swr'
import Link from 'next/link'
import Layout from '../../components/layout'
import {useState} from "react"

import $ from 'jquery'
const fetcher = url => fetch(url).then(res => res.json())

export default function Projets() {
    const { data, error } = useSWR('/api/projets', fetcher)
    
    if (error) return <div>Failed to load</div>
    if (!data) return <div>Loading...</div>

    

    const index = $(window).on('mousewheel', () => {
        const position  = $('.projets').scrollTop();
        const height = $(window).height()
        return Math.round(position/height)
    });


    return (
        <Layout>
            <main className="projets" style={{backgroundImage:`url(${data[index].images[0]})`}}>
                <nav>
                    <Link href="/"><a>Retour a l'acceuil</a></Link>
                </nav>
                {
                    data.map(({titre, images, link}, index) => {
                        return (
                            <div key={titre} id={index}>
                                <div>
                                    <h1>{titre}</h1>
                                    <Link 
                                        href="/projets/[projet]" 
                                        as={`/projets/${link}`}
                                    >
                                        <a>Voir projet</a>
                                    </Link>
                                </div>
                            </div>
                        )
                    })
                }
            </main>
        </Layout>
    )
}
