import fetch from 'isomorphic-fetch'
import Link from 'next/link'
import {getAllProjectsLinks} from '../../lib/getAllProjectsLinks'

export default function Projet({titre, description,images }){
    return(
        <div className="projet">
            <nav>
                <h1>
                    {titre}
                </h1>
                <Link href="/projets">
                    <a>
                        Retour aux projets
                    </a>
                </Link>
            </nav>
            <div>
            {
                images.map((image) => {
                    return <img src="image" width="100%"/>
                })
            }
            <h3>Description</h3> 
            <p>{description}</p>
            </div>
            
        </div>
    ) 
}

export async function getStaticPaths() {
    const res = await fetch('http://localhost:3000/api/projets')
    const projets = await res.json()
    const paths = getAllProjectsLinks(projets)
    return{
        paths,
        fallback: false
    }
}

export async function getStaticProps({params}) {
    const res = await fetch('http://localhost:3000/api/projets')
    const projets = await res.json()
    const findProjet = projets.filter((projets) => projets.link === params.projet)
    return {
        props: findProjet[0]
    }
}
