import { projects } from '../../data'

export default ({ query: { link } }, res) => {
  const filtered = projects.filter(p => p.link === link)

  // User with id exists
  if (filtered.length > 0) {
    res.status(200).json(filtered[0])
  } else {
    res.status(404).json({ message: `Project with id: ${link} not found.` })
  }
}
